import { Component } from '@angular/core';
import { CurrencyAPIDataService } from './service/currency-api-data.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'Currency Swap';

  currJSON: any = [];
  base = 'USD';
  rightCurrency = 'USD';
  result: string = '1';
  numberToConvert: number = 0;

  changeBase(a: string) {
    this.base = a;
  }

  toCountry(b: string) {
    this.rightCurrency = b;
  }

  constructor(private currency: CurrencyAPIDataService) {}

  convert(digit: number) {
    this.numberToConvert = digit;
    this.currency.getCurrencyData(this.base).subscribe(data => {
      this.currJSON = JSON.stringify(data);
      this.currJSON = JSON.parse(this.currJSON);
      // this.result = this.currJSON.rates.UAH.toFixed(2)
      // console.log(+this.result * this.numberToConvert)
    })
  }
}
