import { Component } from '@angular/core';
import { CurrencyApiDataService } from './currencyapidata.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'Currency Swap';
  currJson: any = [];

  base = 'USD';
  cont2 = 'USD';
  result: string = '1'

  changebase(a: string) {
    this.base = a;
    console.log(this.base)
  }
}
